
$(document).ready(function () {

    var w_size;

    $('#upload').on('change', function () {
        var reader = new FileReader();
        reader.onload = function (e) {

            var img = new Image();
            img.src = e.target.result;

            img.onload = function () {
                w_size = Math.min(img.width, img.height);

                if (w_size > 600) {
                    w_size = 600;
                }

                $uploadCrop.croppie('destroy');

                $uploadCrop = $('#upload-demo').croppie({
                    enableExif: true,
                    viewport: {
                        width: w_size,
                        height: w_size,
                        type: 'square'
                    },
                    boundary: {
                        width: 600,
                        height: 600
                    },
                    enableZoom: true,
                    enforceBoundary: true
                });

                $uploadCrop.croppie('bind', {
                    url: e.target.result,
                    zoom: 0
                }).then(function () {
                    console.log('jQuery bind complete');
                });
            };
        };
        reader.readAsDataURL(this.files[0]);
    });


    $uploadCrop = $('#upload-demo').croppie({
        enableExif: true,
        viewport: {
            width: 300,
            height: 300,
            type: 'square'
        },
        boundary: {
            width: 600,
            height: 600
        },
        enableZoom: true,
        enforceBoundary: true
    });

    $('.upload-result').on('click', function (ev) {
        $uploadCrop.croppie('result', {
            type: 'canvas',
            size: 'viewport'
        }).then(function (resp) {

            default_img = "data:image/png;base64,";
            if (resp == default_img) {
                $('.pessimistic').text('Please upload image.');
                return;
            }
            $.ajax({
                type: 'POST',
                url: '/api/predict',
                data: resp,
                dataType: 'json',
                contentType: false,
                cache: false,
                async: false,
                success: function (resp) {
                    if (resp['error'] != undefined){
                        alert(resp['error']);
                        return;
                    }
                    $('.optimistic').text('Optimistic: ' + resp['Optimistic']);
                    $('.pessimistic').text('Pessimistic: ' + resp['Pessimistic']);
                    $('.real').text('Real: ' + resp['real']);
                    $("#status").text(resp);
                },
            });
        });
    });
    $(".cr-image").prop("alt", "");

    $(".browse-btn").click(function () {
        $('#upload').click();
    });

    $(".create-pdf").click(function () {

        const filename  = 'report.pdf';

        html2canvas(document.querySelector('#nodeToRenderAsPDF')).then(canvas => {
            // debugger;
			let pdf = new jsPDF('p', 'mm', 'a4');
			pdf.addImage(canvas.toDataURL('image/png'), 'PNG',-60, -30, 300, 170);

			var offset = 150;
			pdf.text($('.pessimistic').text(), 50, offset, 'left');
			pdf.text($('.optimistic').text(), 50, offset + 10, 'left');
			pdf.text($('.real').text(), 50, offset + 20, 'left');
			pdf.save(filename);
		});

    });



    $(".reset-pass-btn").click(function () {
        // debugger;
        if ($("input[name=new_pass]").val() != $("input[name=conf_pass]").val()) {
            alert('new password and confirm password is not equal.');
            return;
        }

        else {
            $(".edit-pass-form").submit();
        }

    });

});